const contactsButton=document.querySelector(".contacts-button"),modal=document.querySelector(".modal"),modalCloseButton=document.querySelector(".modal-close"),nameInput=modal.querySelector("[name=name]"),emailInput=modal.querySelector("[name=email]"),textInput=modal.querySelector("[name=text]"),modalForm=modal.querySelector(".appointment-form");let isStorageSupport=!0,name="",email="";try{name=localStorage.getItem("name"),email=localStorage.getItem("email")}catch(e){isStorageSupport=!1}contactsButton.addEventListener("click",function(e){e.preventDefault(),modal.classList.add("modal-show"),name&&email?(nameInput.value=name,emailInput.value=email,textInput.focus()):nameInput.focus()}),modalCloseButton.addEventListener("click",function(e){e.preventDefault(),modal.classList.remove("modal-show"),modal.classList.remove("modal-error")}),modalForm.addEventListener("submit",function(e){nameInput.value&&emailInput.value&&textInput.value?isStorageSupport&&(localStorage.setItem("name",nameInput.value),localStorage.setItem("email",emailInput.value)):(e.preventDefault(),modal.classList.remove("modal-error"),modal.offsetWidth=modal.offsetWidth,modal.classList.add("modal-error"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&modal.classList.contains("modal-show")&&(e.preventDefault(),modal.classList.remove("modal-show"),modal.classList.remove("modal-error"))})